import React from 'react';
import { Redirect } from 'react-router-dom'
import { getTokenClient,getTokenAuth, getFavorites } from '../index/token'
import SearchBar from '../subComponent/SearchBar';
import { TablePagination, Grid, Button } from '@material-ui/core';
import CardList from '../subComponent/CardList';
import TitleBar from '../subComponent/TitleBar';
import ArrowBackIcon from '@material-ui/icons/ArrowBack';
import { getUniversityFunction } from './saga';

class NewsletterUniversityList extends React.Component {
    _isMounted = false

    state = {
        favoriteUniversityList: [],
        searchRows:'',
        page: 0,
        rowsPerPage: 12,
    };

    componentDidMount(){
        this._isMounted=true;
        this._isMounted && this.getListUniversity()
        this._isMounted && this.getDataFavoriteFromLocalStorage()
    }

    componentWillUnmount(){
        this._isMounted=false
    }

    getListUniversity = async function(){
        if(!this.state.universityList) {
          const data = await getUniversityFunction({});
    
          if(data){
            if(!data.error){
              this.setState( { universityList: data.data })
            }else{
              this._isMounted && this.setState({errorMessage:data.error})
            }
          }     
        }
      }

    getDataFavoriteFromLocalStorage = () => {
        const dataUser = getFavorites();

        if(dataUser) {
            this.setState( { favoriteUniversityList: dataUser} )
        }
    }

    saveData = (data, fileName) => { 
        const a = document.createElement("a"); 
        document.body.appendChild(a); 
        a.style = "display: none"; 
        const json = JSON.stringify(data);
        const blob = new Blob([json], {type: "octet/stream"});
        const url = window.URL.createObjectURL(blob); 
        a.href = url; 
        a.download = fileName; 
        a.click(); 
        window.URL.revokeObjectURL(url); 
    };

    downloadFileJSON = () => {

        const data = { x: 42, s: "hello, world", d: new Date() }, 
        fileName = "users.json"; 

        this.saveData(data, fileName); 
    }
  
    render() {

        if(getTokenClient() && getTokenAuth()){
            return (
                <div style={{padding:0}}>
                <TitleBar 
                    title="Newsletter University - List"
                />
                <Grid container>
                    <Grid item xs={3} lg={3}>
                        <Button
                            variant="outlined"
                            color="primary"
                            startIcon={
                                <ArrowBackIcon />
                            }
                            onClick={() => this.downloadFileJSON(this.state.allUniversityList)}
                        >
                            Download all university
                        </Button>
                    </Grid>
                    <Grid item xs={3} lg={3}>
                        <Button
                            variant="outlined"
                            color="primary"
                            startIcon={
                                <ArrowBackIcon />
                            }
                            onClick={() => this.downloadFileJSON(this.state.favoriteUniversityList)}
                        >
                            Download my favorite university
                        </Button>
                    </Grid>
                </Grid>
                
{/* 
                <SearchBar
                    id="search"
                    value={this.state.searchRows}
                    placeholder={'Search University Name'}
                    onChange={this.handleChangeSearch} 
                    float={'right'}
                />

                <Grid container>
                    {
                        this.state.favoriteUniversityList &&
                        this.searchUniversityName(this.state.favoriteUniversityList, this.state.searchRows)
                        .slice(this.state.page * this.state.rowsPerPage, this.state.page * this.state.rowsPerPage + this.state.rowsPerPage)
                        .map((university, index) => 
                            <Grid item xs={12} sm={4} lg={4} key={index} style={{ padding: 10 }}>
                            <CardList
                                data={university}
                            />
                            </Grid>
                        )
                    }
                </Grid>

                <TablePagination
                    component="div"
                    count={this.searchUniversityName(this.state.favoriteUniversityList, this.state.searchRows).length}
                    rowsPerPage={this.state.rowsPerPage}
                    page={this.state.page}
                    onChangePage={this.handleChangePage}
                    rowsPerPageOptions={[]}
                    labelDisplayedRows={
                        ({ from, to, count }) => `Rows: ${from}-${to} of ${count !== -1 ? count : `more than ${to}`}`
                    }
                />
                */}
                </div> 
            );
        }
        else if(getTokenAuth()){
            return  <Redirect to='/login' />
        }
    }
}

export default (NewsletterUniversityList) ;
