import React from 'react';
import {connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import {getUniversityFunction} from './saga'
import { getTokenClient,getTokenAuth } from '../index/token'
import SearchBar from '../subComponent/SearchBar';
import { TablePagination, Grid } from '@material-ui/core';
import CardList from '../subComponent/CardList';
import TitleBar from '../subComponent/TitleBar';
import Typography from 'material-ui/styles/typography';

class UniversityDetail extends React.Component {
  _isMounted = false

  state = {
    universityList: null,
    searchRows:'',
    page: 0,
    rowsPerPage: 12,
  };

  componentDidMount(){
    this._isMounted=true
    this._isMounted && this.getUniversityDetail()
  }

  componentWillUnmount(){
    this._isMounted=false
  }

  //Ambil data
  getUniversityDetail = async function(){
    const domain =  this.props.match.params.id

    if(!this.state.universityList) {
      const data = await getUniversityFunction({});

      if(data){
        if(!data.error){
            const newData = data.data || [];

            this.setState( { universityList: this.searchDetail(newData, domain) })
        }else{
            this._isMounted && this.setState({errorMessage:data.error})
        }
      }     
    }
  }

  handleChangeSearch = (e)=>{
    this.setState({searchRows: e.target.value});
  }

  handleChangePage = (event, newPage) => {
    this.setState({page: newPage});
  };

  searchDetail = (listUniversity, search) => {
    const newList = listUniversity && listUniversity.filter(function (university) {
      return university.domains && university.domains[0].toLowerCase() === search.toLowerCase()
    });
    console.log(newList)
    return newList || [];
  }

  render() {
    if(getTokenClient() && getTokenAuth()){
      return (
        <div style={{padding:0}}>
            <TitleBar 
                title="University - Detail"
            />

            {
                this.state.universityList &&
                <Grid container style={{ marginTop: 20, padding: 20 }}>
                    <Grid item xs={2} lg={2}>
                        <Typography variant="h3"> University Name </Typography>
                    </Grid>
                    <Grid item xs={10} lg={10}>
                        <Typography variant="h3"> { this.state.universityList.name } </Typography>
                    </Grid>
                    <Grid item xs={2} lg={2}>
                        <Typography variant="h3"> Country </Typography>
                    </Grid>
                    <Grid item xs={10} lg={10}>
                        <Typography variant="h3"> { this.state.universityList.country } </Typography>
                    </Grid>
                    <Grid item xs={2} lg={2}>
                        <Typography variant="h3"> Country Code </Typography>
                    </Grid>
                    <Grid item xs={10} lg={10}>
                        <Typography variant="h3"> { this.state.universityList.name } </Typography>
                    </Grid>
                    <Grid item xs={2} lg={2}>
                        <Typography variant="h3"> Domain </Typography>
                    </Grid>
                    <Grid item xs={10} lg={10}>
                        <Typography variant="h3"> Website </Typography>
                    </Grid>
                    <Grid item xs={2} lg={2}>
                        <Typography variant="h3"> Website </Typography>
                    </Grid>
                    <Grid item xs={10} lg={10}>
                        <Typography variant="h3"> Website </Typography>
                    </Grid>
                </Grid>
            }

          {/* <SearchBar
            id="search"
            value={this.state.searchRows}
            placeholder={'Search University Name'}
            onChange={this.handleChangeSearch} 
            float={'right'}
          />

          <Grid container>
            {
              this.state.universityList &&
              this.searchUniversityName(this.state.universityList, this.state.searchRows)
              .slice(this.state.page * this.state.rowsPerPage, this.state.page * this.state.rowsPerPage + this.state.rowsPerPage)
              .map((university, index) => 
                <Grid item xs={12} sm={4} lg={4} key={index} style={{ padding: 10 }}>
                  <CardList
                    data={university}
                  />
                </Grid>
              )
            }
          </Grid>

          <TablePagination
            component="div"
            count={this.searchUniversityName(this.state.universityList, this.state.searchRows).length}
            rowsPerPage={this.state.rowsPerPage}
            page={this.state.page}
            onChangePage={this.handleChangePage}
            rowsPerPageOptions={[]}
            labelDisplayedRows={
              ({ from, to, count }) => `Rows: ${from}-${to} of ${count !== -1 ? count : `more than ${to}`}`
            }
          />
         */}
        </div>
      );
    }
    else if(getTokenAuth()){
      return  <Redirect to='/login' />
    }
  }
}

const mapStateToProp = (state)=>{
  return{     
      id: state.user.id
  }
}
export default connect (mapStateToProp)(UniversityDetail) ;
