import React from 'react';
import { Redirect } from 'react-router-dom'
import {getUniversityFunction} from './saga'
import { getTokenClient,getTokenAuth, getProfileUser } from '../index/token'
import SearchBar from '../subComponent/SearchBar';
import { TablePagination, Grid, FormControl, InputLabel, Select, Input } from '@material-ui/core';
import CardList from '../subComponent/CardList';
import TitleBar from '../subComponent/TitleBar';
import { Chip, MenuItem } from 'material-ui';

class FavoriteUniversityList extends React.Component {
    _isMounted = false

    state = {
        favoriteUniversityList: [],
        allUniversityList: [],
        searchRows:'',
        page: 0,
        rowsPerPage: 12,
    };

    componentDidMount(){
        this._isMounted=true
        this._isMounted && this.getListUniversity()
        // this._isMounted && this.getDataFavoriteFromLocalStorage();
    }

    componentWillUnmount(){
        this._isMounted=false
    }
  
    //Ambil data
    getListUniversity = async function(){
        if(!this.state.favoriteUniversityList) {
            const data = await getUniversityFunction({});
            console.log('wew')
            if(data){
                if(!data.error){
                    this.setState( { allUniversityList: data.data })
                }else{
                    this._isMounted && this.setState({errorMessage:data.error})
                }
            }     
        }
    }

    getDataFavoriteFromLocalStorage = () => {
        const dataUser = JSON.parse(getProfileUser());

        if(dataUser.favorites) {
            this.setState( { favoriteUniversityList: dataUser.favorites} )
        }
    }

    handleChangeSearch = (e)=>{
        this.setState({searchRows: e.target.value});
    }
    
    handleChangePage = (event, newPage) => {
        this.setState({page: newPage});
    };

    searchUniversityName = (listUniversity, search) => {
        const newList = listUniversity && listUniversity.filter(function (university) {
        return university.name.toLowerCase().includes(search.toLowerCase())
        });
    
        return newList || [];
    }

    handleChangeMultipleSelect = (event) => {
        this.setState({ favoriteUniversityList: event.target.value });
      };
  
    render() {
        const MenuProps = {
            PaperProps: {
              style: {
                maxHeight: 48 * 4.5 + 8,
                width: 250,
              },
            },
        };

        if(getTokenClient() && getTokenAuth()){
            return (
                <div style={{padding:0}}>
                <TitleBar 
                    title="Favorite University - List"
                />

                <SearchBar
                    id="search"
                    value={this.state.searchRows}
                    placeholder={'Search University Name'}
                    onChange={this.handleChangeSearch} 
                    float={'right'}
                />

                <FormControl>
                    <InputLabel id="demo-mutiple-chip-label">Add More Favorite</InputLabel>
                    <Select
                        labelId="demo-mutiple-chip-label"
                        id="demo-mutiple-chip"
                        multiple
                        value={this.state.favoriteUniversityList}
                        onChange={this.handleChangeMultipleSelect}
                        input={<Input id="select-multiple-chip" />}
                        renderValue={(selected) => (
                            <div>
                                {selected.map((value) => (
                                    <Chip key={value} label={value} />
                                ))}
                            </div>
                        )}
                        MenuProps={MenuProps}
                    >
                        {this.state.allUniversityList.map((university) => (
                            <MenuItem key={university.name} value={university.name}>
                                {university.name}
                            </MenuItem>
                        ))}
                    </Select>
                </FormControl>

                <Grid container>
                    {
                    this.state.favoriteUniversityList &&
                    this.searchUniversityName(this.state.favoriteUniversityList, this.state.searchRows)
                    .slice(this.state.page * this.state.rowsPerPage, this.state.page * this.state.rowsPerPage + this.state.rowsPerPage)
                    .map((university, index) => 
                        <Grid item xs={12} sm={4} lg={4} key={index} style={{ padding: 10 }}>
                        <CardList
                            data={university}
                        />
                        </Grid>
                    )
                    }
                </Grid>

                <TablePagination
                    component="div"
                    count={this.searchUniversityName(this.state.favoriteUniversityList, this.state.searchRows).length}
                    rowsPerPage={this.state.rowsPerPage}
                    page={this.state.page}
                    onChangePage={this.handleChangePage}
                    rowsPerPageOptions={[]}
                    labelDisplayedRows={
                    ({ from, to, count }) => `Rows: ${from}-${to} of ${count !== -1 ? count : `more than ${to}`}`
                    }
                />
                
                </div>
            );
        }
        else if(getTokenAuth()){
            return  <Redirect to='/login' />
        }
    }
}

export default (FavoriteUniversityList) ;
