import React from 'react';
import {connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import './../../support/css/pagination.css'
import {getUniversityFunction} from './saga'
import 'moment/locale/id';
import { getTokenClient,getTokenAuth } from '../index/token'
import TableComponent from './../subComponent/TableComponent'
import { checkPermission } from '../global/globalFunction';
import SearchBar from '../subComponent/SearchBar';

const columnDataUser = [
  {
      id: 'id',
      numeric: false,
      label: 'ID Nasabah',
  },
  {
      id: 'fullname',
      numeric: false,
      label: 'Nama Nasabah',
  },
  {
      id: 'category',
      numeric: false,
      label: 'Kategori',
  },
  {
    id: 'created_at',
    numeric: false,
    type: 'datetime',
    label: 'Tanggal Registrasi',
  },
  {
    id: 'loan_status',
    numeric: false,
    label: 'Status Pinjaman Nasabah',
  },
]

class profileNasabah extends React.Component {

  _isMounted = false
  state = {
    universityList: [],
    universityListPerPage: [],
    searchRows:'',
    page: 1,
    rowsPerPage: 10,
    isEdit: false,
    total_data:0,
    last_page:1,
    loading:true,
  };
  //-----------------------------------NIKO FUNCTION-------------------------------------------------------------
  componentDidMount(){
    this._isMounted=true
    this._isMounted && this.getListUniversity()
  }

  componentWillUnmount(){
    this._isMounted=false
  }

  constructDataUniversity = (data, param) => {

  }

  //Ambil data
  getListUniversity = async function(){


    if(!this.state.universityList) {
      const data = await getUniversityFunction();

      if(data){
        if(!data.error){
          this.setState( { universityList: data, total_data: data.length })
          await this.constructDataUniversity(data);
        }else{
          this._isMounted && this.setState({errorMessage:data.error, loading:false})
        }
      }

    } else {
      const param ={
        rowsPerPage:this.state.rowsPerPage,
        page:this.state.page
      }

      if(this.state.searchRows){
        param.search = this.state.searchRows
      }

      await this.constructDataUniversity(this.state.universityList, param);
    }



  }

  onChangeSearch = (e)=>{
    this.setState({searchRows:e.target.value});
  }

  onBtnSearch = (e)=>{
    this.setState({loading:true, page:1},()=>{
      this.getListUniversity()
    })
  }

  onChangePage = (current) => {

    this.setState({loading:true,page:current},()=>{

      if(this.state.paging){
        this.getListUniversity()
      }
    })
  }

  render() {
    if(getTokenClient()&&getTokenAuth()){
      return (
        <div style={{padding:0}}>
          <SearchBar
              id="search"
              value={this.state.searchRows}
              placeholder={'Search University Name'}
              onChange={this.onBtnSearch} 
              float={'right'}
            />

          < TableComponent
            id={"id"}
            title={'University - List'}
            search={
              {
                value: this.state.searchRows,
                label: 'Search ID Nasabah, Nama Nasabah',
                function: this.onBtnSearch,
              }
            }
            errorMessage={this.state.errorMessage}
            paging={this.state.paging}
            loading={this.state.loading}
            columnData={columnDataUser}
            data={this.state.universityList}
            page={this.state.page}
            rowsPerPage={this.state.rowsPerPage}
            totalData={this.state.total_data}
            onChangePage={this.onChangePage}             
            permissionDetail={ checkPermission('lender_borrower_list_detail') ? '/profileNasabahDetail/' : null}
          /> 

        </div>
      );
    }
    else if(getTokenAuth()){
      return  <Redirect to='/login' />
    }
  }
}

const mapStateToProp = (state)=>{
  return{     
      id: state.user.id
  }
}
export default connect (mapStateToProp)(profileNasabah) ;
