import React from 'react';
import {connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import './../../support/css/pagination.css'
import {getUniversityFunction} from './saga'
import 'moment/locale/id';
import { getTokenClient,getTokenAuth } from '../index/token'
import TableComponent from './../subComponent/TableComponent'
import { checkPermission } from '../global/globalFunction';
import SearchBar from '../subComponent/SearchBar';
import { TablePagination, Grid } from '@material-ui/core';
import CardList from '../subComponent/CardList';

const columnDataUser = [
  {
      id: 'id',
      numeric: false,
      label: 'ID Nasabah',
  },
  {
      id: 'fullname',
      numeric: false,
      label: 'Nama Nasabah',
  },
  {
      id: 'category',
      numeric: false,
      label: 'Kategori',
  },
  {
    id: 'created_at',
    numeric: false,
    type: 'datetime',
    label: 'Tanggal Registrasi',
  },
  {
    id: 'loan_status',
    numeric: false,
    label: 'Status Pinjaman Nasabah',
  },
]

class profileNasabah extends React.Component {
  _isMounted = false

  state = {
    universityList: null,
    universityListPerPage: null,
    searchRows:'',
    page: 0,
    rowsPerPage: 12,
    isEdit: false,
    total_data: 0,
    loading:true,
  };
  //-----------------------------------NIKO FUNCTION-------------------------------------------------------------
  componentDidMount(){
    this._isMounted=true
    this._isMounted && this.getListUniversity()
  }

  componentWillUnmount(){
    this._isMounted=false
  }

  //Ambil data
  getListUniversity = async function(){
    if(!this.state.universityList) {
      const data = await getUniversityFunction();

      if(data){
        if(!data.error){
          this.setState( { universityList: data.data, total_data: (data.data && data.data.length) || 0 })
        }else{
          this._isMounted && this.setState({errorMessage:data.error})
        }
      }

    } else {
      const param ={
        rowsPerPage:this.state.rowsPerPage,
        page:this.state.page
      }

      if(this.state.searchRows){
        param.search = this.state.searchRows
      }

      await this.constructDataUniversity(this.state.universityList, param);
    }

  }

  onChangeSearch = (e)=>{
    this.setState({searchRows:e.target.value});
  }

  handleChangePage = (event, newPage) => {
    this.setState({page: newPage});
  };

  render() {
    if(getTokenClient() && getTokenAuth()){
      return (
        <div style={{padding:0}}>
          <SearchBar
            id="search"
            value={this.state.searchRows}
            placeholder={'Search University Name'}
            onChange={this.onBtnSearch} 
            float={'right'}
          />

          <Grid container>
            {
              this.state.universityList &&
              this.state.universityList
              .slice(this.state.page * this.state.rowsPerPage, this.state.page * this.state.rowsPerPage + this.state.rowsPerPage)
              .map((university, index) => 
                <CardList
                id={index}
                  data={university}
                />
              )
            }
          </Grid>

          <TablePagination
            component="div"
            count={this.state.total_data}
            rowsPerPage={this.state.rowsPerPage}
            page={this.state.page}
            onChangePage={this.handleChangePage}
          />

        </div>
      );
    }
    else if(getTokenAuth()){
      return  <Redirect to='/login' />
    }
  }
}

const mapStateToProp = (state)=>{
  return{     
      id: state.user.id
  }
}
export default connect (mapStateToProp)(profileNasabah) ;
